import React, { useState } from 'react';

const orders = [
  { id: 1, status: 'Chờ xác nhận' },
  { id: 2, status: 'Đã xác nhận' },
  { id: 3, status: 'Đang giao' },
  { id: 4, status: 'Đã giao' },
  { id: 5, status: 'Huỷ' },
  // Add more orders here
];

const itemsPerPage = 3; // Number of orders to display per page

const QuanLyDonHang = () => {
  const [selectedStatus, setSelectedStatus] = useState('Chờ xác nhận');
  const [currentPage, setCurrentPage] = useState(1);

  const filteredOrders = orders.filter((order) => order.status === selectedStatus);
  const totalPages = Math.ceil(filteredOrders.length / itemsPerPage);

  const handleChangeStatus = (status) => {
    setSelectedStatus(status);
    setCurrentPage(1); // Reset to the first page when status changes
  };

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  const displayedOrders = filteredOrders.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  return (
    <div className="QuanLyDonHang">
      <h1>Quản lý đơn hàng</h1>
      <div>
        <label htmlFor="status">Chọn trạng thái:</label>
        <select
          id="status"
          value={selectedStatus}
          onChange={(e) => handleChangeStatus(e.target.value)}
        >
          <option value="Chờ xác nhận">Chờ xác nhận</option>
          <option value="Đã xác nhận">Đã xác nhận</option>
          <option value="Đang giao">Đang giao</option>
          <option value="Đã giao">Đã giao</option>
          <option value="Huỷ">Huỷ</option>
        </select>
      </div>
      <div>
        <h2>Danh sách đơn hàng ({selectedStatus})</h2>
        <ul>
          {displayedOrders.map((order) => (
            <li key={order.id}>{`Đơn hàng ${order.id} - Trạng thái: ${order.status}`}</li>
          ))}
        </ul>
        <div className="pagination">
          {Array.from({ length: totalPages }, (_, index) => (
            <label key={index}>
              <input
                type="checkbox"
                checked={currentPage === index + 1}
                onChange={() => handlePageChange(index + 1)}
              />{' '}
              {index + 1}
            </label>
          ))}
        </div>
      </div>
    </div>
  );
};

export default QuanLyDonHang;
