import IndexHeadWeb from "../HeadWeb/IndexHeadWeb";
import { useEffect, useState } from "react";

function Context() {
  const [dsLoai_San_Pham, setDSLoai_San_Pham] = useState([]);

  useEffect(() => {
    fetch('http://localhost:9000/get-data.php')
      .then(response => response.json())
      .then(json => setDSLoai_San_Pham(json))
      .catch(error => console.error('Lỗi khi lấy dữ liệu:', error));
  }, []);

  // Kiểm tra xem dữ liệu đã tải xong chưa
  if (dsLoai_San_Pham.length === 0) {
    return (
      <>
        <IndexHeadWeb />
        <p>Đang tải dữ liệu...</p>
      </>
    );
  }

  // Dữ liệu đã tải xong, hiển thị danh sách loại sản phẩm
  return (
    <>
      <IndexHeadWeb />
      <div>
        <h1>Danh sách loại sản phẩm:</h1>
        <ul>
          {dsLoai_San_Pham.map((loaiSanPham, index) => (
            <li key={index}>{loaiSanPham.ten}</li>
          ))}
        </ul>
      </div>
    </>
  );
}

export default Context;
