import IndexHeadWeb from "../HeadWeb/IndexHeadWeb";
import Loai_San_Pham from "../ContentWeb/Loai_San_Pham";
import { useEffect, useState } from "react";

function Context() {
  const [dsLoai_San_Pham, setDSLoai_San_Pham] = useState([]);

  useEffect(() => {
    fetch('http://localhost:9000/get-data.php')
      .then(response => response.json())
      .then(json => setDSLoai_San_Pham(json))
      .catch(error => console.error('Lỗi khi lấy dữ liệu:', error));
  }, []);

  return (
    <>
      <IndexHeadWeb />
      <div>
        <h1>Danh sách loại sản phẩm:</h1>
        <ul>
          {dsLoai_San_Pham.map((loaiSanPham, index) => (
            <li key={index}>
              <h2>{loaiSanPham.ten}</h2>
              <ul>
                {loaiSanPham.ds_san_pham.map((sanPham, sanPhamIndex) => (
                  <li key={sanPhamIndex}>
                    <img src={sanPham.img} alt={sanPham.name} />
                  </li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      </div>
    </>
  );
}

export default Context;
