import IndexHeadWeb from "../HeadWeb/IndexHeadWeb";
import { useEffect, useState } from "react";

function Context() {
  const [dsLoai_San_Pham, setDSLoai_San_Pham] = useState([]);

  useEffect(() => {
    fetch('http://localhost:9000/get-data.php')
      .then(response => response.json())
      .then(json => setDSLoai_San_Pham(json))
      .catch(error => console.error('Lỗi khi lấy dữ liệu:', error));
  }, []);

  // Kiểm tra xem dsLoai_San_Pham có dữ liệu hay không
  if (dsLoai_San_Pham.length === 0) {
    return (
      <>
        <IndexHeadWeb />
        <div>
          <p>Đang tải dữ liệu...</p>
        </div>
      </>
    );
  }

  // Lấy giá trị 'ten' của loại sản phẩm đầu tiên
  const Loai = dsLoai_San_Pham[0].ten;

  return (
    <>
      <IndexHeadWeb />
      <a>{Loai}</a>
    </>
  );
}

export default Context;
